//程式功能：动态数组的实现//功能描述：动态数组的创建与使用#include<stdio.h>#include<stdlib.h>void main(){	int *array = 0, num=5, i;	int *carray;	printf("please input the number of element: %d\n", num);	//scanf("%d", &num);	//申请动态数组使用的内存块	array = (int *)malloc(sizeof(int)*num);	carray = (int *)calloc(sizeof(int),num+1);	if(array==0){		printf("out of memory, press any key to quit...\n");		exit(0);	}	//使用malloc后分配内存的初始化值	printf("malloc array init: %d %o 0x%x\n", array[0], array[1], array[num]);	printf("calloc array init: %d %o 0x%x\n", carray[0], carray[1], carray[2]);	//提示输入num个数据	printf("please input %d elements:\n", num);	for(i=0;i<num;i++){		array[i] = num+i;		//printf("element %d:",i+1);		//scanf("%d", &array[i]);	}	//输出刚输入的num个数据	printf("%d elements are:\n",num);	for(i=0;i<num;i++){		printf("%d,",array[i]);	}	printf("\b \n");//删除最后一个数字后的分隔符逗号	free(array);//释放由malloc函数申请的内存块}