typedef float elemType;//别名声明，其实int可以用任意的数据类型代入#define NL "\n" //格式化输出函数#define D "%3.2f "#define DL D NL#include "stack.c"void main(){	//动态设置将十进制转换为其他的进制	#define De 2	#define N 5	sLink S=initStack();	//进制转换	void conversion(sLink,int);	elemType arr[N] = {3.5,1.5,2.5,-6.9,-7.9};	int i,M;	//压入栈	for(i=0;i<N;i++){		push(S,arr[i]);	}	//sizeof用法	//printf("%d %d",sizeof(int),sizeof i);	//输出栈	printf("The stack length is %d\n",stackLength(S));	stackOut(S);	//栈顶元素	printf("\nThe top elem of stack is:"D,getTop(S));	//弹出栈	pop(S);	printf("\n\nAfter poping from stack,length is %d:\n",stackLength(S));	stackOut(S);	//清空栈	printf("\n\n");	clearStack(S);	if(stackEmpty(S)) printf("The Stack is empty!");	stackOut(S);//null	//十进制转换	M = 1348;	conversion(S,M);}//进制转换void conversion(sLink S,int change){	//对于输入的任意一个非负十进制整数，打印输出与其等值的八进制数	if(change<0) return;	while(change){		push(S,change%De);//将每次余数压入栈中		change = change/De;	}	printf("\n\nprint the stack:\n");	stackOut(S);	printf("\n\nprint the stack:\n");	while(!stackEmpty(S)){		printf("%d ",(int)pop(S));	}	if(stackEmpty(S)) printf("\nThe Stack is empty!");}