typedef char elemType;//别名声明，其实int可以用任意的数据类型代入#define NL "\n" //格式化输出函数#define D "%3.2c "#define DL D NL#include "stack.c"void main(){	sLink S=initStack();	elemType c,d;	printf("Please enter the match string:\n");	c = getchar();	/*括号匹配的监测*/	while(c!='\n'){		switch(c){			case '[':				push(S,c);				break;			case '(':				push(S,c);				break;			case ')':				d = pop(S);				if(d!='('){//判断栈中最近的元素是不是(,方便匹配)					printf("It is not match!\n");					return;				}				break;			case ']':				d = pop(S);				if(d!='['){//判断栈中最近的元素是不是],方便匹配];					printf("It is not match!\n");					return;				}				break;		}		c = getchar();	}	if(stackEmpty(S)) printf("It is match!\n");	else printf("It is not match!\n");	printf("The length is:%d",stackLength(S));}