typedef char elemType;//别名声明，其实int可以用任意的数据类型代入#define NL "\n" //格式化输出函数#define D "%c"#define DL D NL#include "stack.c"void main(){	/*行编辑程序:利用字符栈S，从终端接受一行并传送至调用过程的数据区*/	sLink S=initStack();//构造空栈S	elemType ch,d;	ch = getchar();//从终端接收第一个字符	//它是end of file的缩写，表示"文字流"（stream）的结尾。	//这里的"文字流"，可以是文件（file），也可以是标准输入（stdin）。	while(ch!=EOF){//EOF为^Z健，全文结束符		//行内循环		while(ch!=EOF&&ch!='\n'){			//也可插入中文，一个#只能删掉半个汉字，剩下一半汉字显示一个?			switch(ch){				case '#'://如果ch是退格符#					d = pop(S);//把栈S的栈顶元素弹出到d					break;//仅当栈非空时退格				case '@'://如果ch是退行符@					clearStack(S);//清空栈					break;//重置S为空栈				default://如果ch不是^Z、\n、#、@，而是其他有效字符					push(S,ch);//那么有效字符ch进栈S					//break;			}			ch = getchar();//从终端接收下一个字符，准备下轮(行内下个字符)循环		}		//将栈底到栈顶的栈内字符传送至调用过程的数据区：		stackOut(S);//输出缓存区输入的数据，一般以行为单元		printf("\n");		clearStack(S);//一行遍历完毕，栈中所有元素已经全写入了缓存区，重置S为空栈，接收下一行数据输入		if(ch!=EOF){//只要ch不是^Z			ch = getchar();//就继续接收字符，进入下轮（行）循环		}	}	//用户输入^Z时循环结束	destroyStack(S);}